    t = block.get\(''type'', ''''\)
    if t == ''thinking'':
        print\(f''THINKING: len={len\(block.get\(""content"", """"\)\)}''\)
        print\(block.get\(''content'', ''''\)[:300]\)
    elif t == ''tool_call'':
        print\(f''TOOL: {block.get\(""name"", ""?""\)}''\)
    elif t == ''text'':
        print\(f''TEXT: len={len\(block.get\(""content"", """"\)\)}''\)
